						'name' => 'university_content',
						'label' => esc_html__( 'University Content', 'textdomain' ),
						'type' => \Elementor\Controls_Manager::WYSIWYG,
						'default' => esc_html__( 'University Content' , 'textdomain' ),
						'show_label' => false,
